mapper.select:

SELECT approval_form as "approvalForm"
FROM approval_category
WHERE approval_category_id = #{approvalCategoryId}

mapper.update:
UPDATE approval SET
	title = #{title}, 
	write_date = sysdate, 
	content = #{content}, 
	approval_category_id = #{approvalCategoryId}, 
	status = default, 
	temp_approval = #{tempApproval}

		, ref_emp_id = #{refEmpId}

WHERE emp_id = #{empId}
	AND approval_id = #{approvalId}

mapper.insert:
SELECT approval_id FROM approval WHERE approval_id = #{approvalId}
SELECT seq_approval.CURRVAL FROM dual
INSERT INTO approval_line (approval_line_id, approval_id, emp_id, seq) VALUES (seq_approval_line.nextval, #{approvalId}, #{empId}, #{seq})

mapper.select:
SELECT count(*)
FROM approval
WHERE temp_approval='n' and emp_id = #{empId}

		AND status = #{status}


		AND approval_category_id = #{approvalCategoryId}

SELECT approval_id as "approvalId", title as "title", a.emp_id as "empId", name as "empName", dept_name as "deptName", write_date as "writeDate", a.approval_category_id as "approvalCategoryId", approval_name as "categoryName", status, temp_approval as "tempApproval" FROM approval a, employees e, approval_category c, departments d WHERE a.emp_id=e.emp_id and e.dept_id = d.dept_id and a.approval_category_id=c.approval_category_id and temp_approval='n' and a.emp_id = #{empId}
AND status = #{status}
AND a.approval_category_id = #{approvalCategoryId}
ORDER BY write_date desc OFFSET #{pager.startRowNo}-1 ROWS FETCH FIRST 10 ROWS ONLY

SELECT count(*)
FROM approval a, approval_line l
WHERE a.approval_id = l.approval_id
    and temp_approval='n' and l.emp_id = #{empId}

		AND status = #{status}


		AND approval_category_id = #{approvalCategoryId}

SELECT l.approval_id as "approvalId", title as "title", a.emp_id as "empId", name as "empName", dept_name as "deptName", write_date as "writeDate", a.approval_category_id as "approvalCategoryId", approval_name as "categoryName", status, temp_approval as "tempApproval" FROM approval a, approval_line l, employees e, approval_category c, departments d WHERE a.emp_id=e.emp_id and e.dept_id = d.dept_id and a.approval_id = l.approval_id and a.approval_category_id=c.approval_category_id and temp_approval='n' and l.emp_id = #{empId}
AND status = #{status}
AND a.approval_category_id = #{approvalCategoryId}
ORDER BY write_date desc OFFSET #{pager.startRowNo}-1 ROWS FETCH FIRST 10 ROWS ONLY

SELECT count(*)
FROM approval
WHERE temp_approval='y' and emp_id = #{empId}

SELECT count(*)
FROM approval
WHERE temp_approval='n' and ref_emp_id = #{empId}

mapper.update:

UPDATE approval
SET status = #{status}
WHERE approval_id = #{approvalLine.approvalId}
